import React, { Component } from 'react'

export class FormTwo extends Component {
    constructor(props) {
        super(props)

        this.state = {
            firstName: ''
        }
        this.handelChange = this.handelChange.bind(this)
        this.handelSubmit = this.handelSubmit.bind(this)

    }

    handelChange(eve) {
        let newValue = eve.target.value;
        this.setState({
            ...this.state,
            [eve.target.name]: newValue
        })
    }

    handelSubmit(eve) {
        eve.preventDefault()
        let allData = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            phoneNumber: this.state.number,
            email: this.state.email
        }
        this.setState({
            allData: allData
        })
    }
    render() {
        if(this.state.allData){
            return div
        }
        return (

            <div className="container">
                <h1>Wellcome</h1>
                <p>Please provide your information below.</p>
                <form onSubmit={this.handelSubmit}>
                    <input type="text" placeholder="First Name" name="firstName" onChange={this.handelChange} />
                    <br /> <input type="text" placeholder="Last Name" name="lastName" onChange={this.handelChange} />
                    <br />  <input type="text" placeholder="Phone number" name="number" onChange={this.handelChange} />
                    <br />  <input type="text" placeholder="Email Address" name="email" onChange={this.handelChange} /><br /> <br /><br />
                    <button type="submit">Submit</button>


                </form>


            </div>
        )
    }
}


export default FormTwo
